import classes from "./QuoteList.module.css";
import { useLoaderData, useSearchParams, useNavigate } from "react-router-dom";
import QuoteItem from "./QuoteItem";
export default function QuoteList() {
  const isAsc = true;
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();

  const data = useLoaderData();
  const allQuote = Object.values(data);

  const sort = searchParams.get("sort");

  let ordinal = sort === "asc" || sort === "" ? 1 : allQuote.length;
  sort === "asc" || sort === "" || allQuote.reverse();

  return (
    <>
      <div className={classes.sorting}>
        {" "}
        <button
          onClick={() => {
            sort === "asc"
              ? setSearchParams({ sort: "desc" })
              : setSearchParams({ sort: "asc" });
          }}
        >
          Sort {sort === "asc" ? "Descending" : "Ascending"}
        </button>
      </div>
      <ul className={classes.list}>
        {allQuote.map((quote) => (
          <QuoteItem
            ordinal={sort === "asc" || sort === "" ? ordinal++ : ordinal--}
            quoteData={quote}
            key={quote.text}
          ></QuoteItem>
        ))}
      </ul>
    </>
  );
}
